package com.app.string;

public class ToCheckCharacterIsAlphabhet {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		
		char ch = 'h';
		
		if(ch >='a'&& ch<='z' || ch >='A' && ch<='Z') {
			System.out.println(ch+" is a alphabhet");
		}
		else {
			System.out.println(ch+" is not a alphabhet");
		}
		

	}

}
